public class Ejercicio4 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int contador=2;
		int suma=1;
		while (contador<=100) {
			suma=suma+contador;
			contador++;
		}
		System.out.println(suma);
	}
}
